import React from "react";
export class ForceUpdting extends React.Component{
   
    constructor(){
        super();
        this.name="Sanitha";
    }
    showMsg(){
        this.name="Parvathy";
        console.log(this.name);

    }
   
    render(){
        return(
          
            <>
          
            <h3>Person Name :{this.name}</h3>
           
            <button onClick={this.showMsg}>Change Name Using ForceUpdate</button>
            </>
        )
    }

}
