import React from "react";
class Content extends React.Component{//class component and default export
    constructor()
    {
        super();
        
       // this.showMsg=this.showMsg.bind(this);
        
    }
    showMsg=(name)=> {

        console.log("clicked",{name});


    }
    render()
    {
       let name="Sanitha"; 
        

        return(
            <>
            <h1>Hiii</h1>
            <button className="btn" onClick={()=>this.showMsg(name)}> Click Me for input capturing!!!!</button>
            </>
        )
    }
}export default Content;


export class ConentCode extends React.Component{//class component and named export
    handleInput(e){
        console.log(e.target.value);
       
    }
    showMsg=(name)=> {
        this.forceUpdate();

        console.log("clicked",{name});


    }
    render()
    {
        return(
            <>
            <h2>Welcome to React</h2>
            <input type="text" onChange={this.handleInput}/>
            <button className="btn" onClick={()=>this.showMsg(this.handleInput)} > Click Me for text input capturing!!!!</button>
            </>
        )
    }
}
