import React, { Component } from 'react'

export default class ReactLifeCycle extends Component {


    constructor(){
        super();
        console.log("1.Construcotr");
        this.state={
            count:10,
            usersList:{}

        }
    }
static getDerivedState(state){
    console.log("2.GetDerivedState");
    return{count:this.state.count+1}//immuttable state
}


componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
        .then(response => response.json()).then(data => this.setState({ usersList: data }))
        .then(json => console.log(json))
}
  render() {
    console.log("3.Rendering");
   
    console.log(this.state.usersList);
    return (
      <>
      <h1>REACT LIFE CYCLE METHODS(Check Console for more information)</h1>
       <h3>~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~</h3>
            {
                this.state.usersList.length && this.state.usersList.map(users => {
                    return (
                        <>
                            
                           
                            <h5 key={users.id}>Name:-{users.name} Email:- {users.email}</h5>
                        </>
                    )
                })
            }
      </>
    )
  }
}
