export class  Events extends React.Component{

    render(){
        function showMsg(){
            console.log("clicked");
    
        }
            
        
        return(
            
        <>
        const btn=document.querySelector('.btn');
        btn.addEventListener('Click',()=>{alert("button clicked")});
      
       <button className="btn" onClick={showMsg}>Click Me!!!!</button>  
     
        </>
    )

    }
    

}
