import React from "react";
export class SetStatement extends React.Component{
   
    constructor(){
        super();
        this.state={//is an object
            name:"Sanitha",
        }
        console.log(this.state.name);
       
    }
    showMsg(){
        this.name="Parvathy";
        console.log(this.name);

    }
    handleName(){
        this.state.name="Parvathy";
        console.log(this.state.name);
    }
    render(){
        return(
          
            <>
            <h3>Person Name using "State" is {this.state.name}</h3>
            <h3>Person Name :{this.name}</h3>
            <button onClick={this.handleName}>Change Name</button>
            <button onClick={this.showMsg}>Change Name Using State</button>
            </>
        )
    }

}
